import React, { useState } from "react";

const WordChoose = () => {
  const [playerSelection, setPlayerSelection] = useState("");

  var Eazy = require("random-words");
  var Medium = require("random-words");
  var Hard = require("random-words");

  Eazy({ exactly: 1, minLength: 3, maxLength: 4 });

  return (
    <div>
      <h2>Please choose between these 3 words: </h2>
      <button onClick={() => {setPlayerSelection("eazy")}}>
        Eazy: {Eazy({ exactly: 1, minLength: 3, maxLength: 4 })}
      </button>
      <button onClick={() => {setPlayerSelection("eazy")}}>
        Medium: {Medium({ exactly: 2, Length: 5 })}
      </button>
      <button onClick={() => {setPlayerSelection("eazy")}}>
        Hard: {Hard({ exactly: 1, minLength: 6 })}
      </button>

    <p>{playerSelection}</p>
    </div>
  );
};

export default WordChoose;
