import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import { getPlayers } from "../axios/axiosFunctions";
import { useQuery } from "react-query";
import Game from "./Game";
import Guess from "./Guess";
import { DrawingState } from "react-canvas-draw/lib/interactionStateMachine";

function useQueryParams() {
    const { search } = useLocation();

    return React.useMemo(() => new URLSearchParams(search), [search]);
  }

const GameManager = () => {

    const [isFinishedDrawing, setIsFinishedDrawing] = useState(false);
    const [playerDrawing, setPlayerDrawing] = useState();
    const [playerSelection, setPlayerSelection] = useState("");
    const [drawing , setDrawing] = useState();

    const onFinishDrawingHandler = (props) => {
        setPlayerDrawing(props[0]);
        setPlayerSelection(props[1]);
        console.log("set to true")
        setIsFinishedDrawing(true);

      };

  const params = useQueryParams();
  const role = params.get("role");
  const gameId = params.get("gameId");
  const { data } = useQuery("players", () => getPlayers(gameId), {
    refetchInterval: 2000,
  });
  //setDrawing(params.get("draw"));

  return (
    <div>
    {console.log("return in gamemanager")}
    {console.log(playerDrawing)}
      {data && data.players === 1 && <div>waiting</div>}
      {data && data.players === 2 && role === "drawing" && <Game onPlayerFinishDrawing={onFinishDrawingHandler}/>}
      {data && data.players === 2 && role === "guessing" && params.get("draw") === null && <p>Wait for player to finish the drawing</p>}
      {data && data.players === 2 && role === "guessing" && params.get("draw") !== null && <p>image is ready</p>}
    </div>
  );
};

export default GameManager;
