import React, { useState, useRef } from "react";
import SelectWord from "./SelectWord";
import CanvasDraw from "react-canvas-draw";

const Game = () => {
  const savedData = null;
  const canvasRef = useRef();
  const [playerSelection, setPlayerSelection] = useState("");

  const wordSelectedHandler = (selectedWord) => {
    setPlayerSelection(selectedWord);
  };

  const getDrawingHandler =() =>{
    savedData = canvasRef.current.getSaveDate();
    canvasRef.current.clear();
  };

  return (
    <div>
      <SelectWord onSelectWord={wordSelectedHandler} />
      <p>The word that was passed to the drawing board: {playerSelection}</p>
      <CanvasDraw brushRadius={2} ref={canvasRef} />
      <button onClick={getDrawingHandler}>save</button>
      <button onClick={()=>{canvasRef.current.loadSaveData(savedData, true)}}>load</button>
    </div>
  );
};

export default Game;
