import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";

const Welcome = (props) => {

  const [time, setTime] = useState(0);
  const channel = new BroadcastChannel("timer_sync");

  const startTimer = useCallback(delay => {
    setTimeout(() => {
      setInterval(() => {
        setTime(time => time + 1);
      }, 1000);
    }, delay);
  }, []);

  const syncItUp = () => {
    const timeNow = new Date().getTime();
    channel.postMessage(timeNow);
    setStatus(`Sent "sync" message`);
    startTimer(2000);
  };

  useEffect(() => {
    channel.onmessage = ev => {
      console.log(ev);
      const timeNow = new Date().getTime();
      setStatus(`Received "sync" message`);
      startTimer(2000 - (timeNow - ev.data));
    };

    return () => {
      channel.close();
    };
  }, [channel, startTimer]);



  const startClickHandler =()=>{
    props.handler();
  };

  return (
    <div>
      <h1>Welcome to our Draw And Guess game!</h1>
      <button onClick={syncItUp}>Click to start</button>
    </div>
  );
};

export default Welcome;
