import React, { useState } from "react";

import "./App.css";
import Game from "./components/Pages/Game";
import Welcome from "./components/Pages/Welcome/Welcome";
import PageSwitcher from "./components/PageSwitcher";

const App = () => {

  const [plyerDrawing, setPlayerDrawing] = useState();

  const onFinishDrawingHandler=(drawing)=>{
    setPlayerDrawing(drawing);
  };

  return (
    <div>
      <Game onPlayerFinishDrawing={onFinishDrawingHandler}/>
      <Guess value={drawing}/>
    </div>
  );
};

export default App;
