import React, { useEffect, useState, useCallback } from "react";

import "./App.css";
import Game from "./components/Pages/Game";
import Guess from "./components/Pages/Guess";
import Welcome from "./components/Pages/Welcome/Welcome";
import PageSwitcher from "./components/PageSwitcher";

const App = () => {
  const channel = new BroadcastChannel("timer_sync");

  const [isFinishedDrawing, setIsFinishedDrawing] = useState(false);
  const [playerDrawing, setPlayerDrawing] = useState();
  const [playerSelection, setPlayerSelection] = useState("");

  const [firstPlayerReady, setFirstPlayerReady] = useState(false);
  const [bothPlayersReady, setBothPlayersReady] = useState(false);

  const onFinishDrawingHandler = (props) => {
    setPlayerDrawing(props[0]);
    setPlayerSelection(props[1]);
    setIsFinishedDrawing(true);
  };

  const bothPlayersReadyHandler = () => {
    setFirstPlayerReady(true);
    setBothPlayersReady(true);
  };

  return (
    <div>
      {!bothPlayersReady && (
        <div>
          <Welcome handler={bothPlayersReadyHandler} />
        </div>
      )}
      {bothPlayersReady && (
        <div>
          <p>Ready to play!</p>
        </div>
      )}
      {/* {!isFinishedDrawing && <Game onPlayerFinishDrawing={onFinishDrawingHandler} />}
      {isFinishedDrawing && (
        <div>
          <Guess value={playerDrawing} word={playerSelection}/>
        </div>
      )} */}
    </div>
  );
};

export default App;
