import React, { useState } from "react";
import { useNavigate } from "react-router-dom";


const WordChoose = () => {
  const navigator = useNavigate();

  const [playerSelection, setPlayerSelection] = useState("");

  const onWordChoose = (event) => {
    setPlayerSelection(event.target.value);
    navigator("/welcome", event.target.value)
  };

  var Eazy = require("random-words");
  var Medium = require("random-words");
  var Hard = require("random-words");

  var firstWord = Eazy({ exactly: 1, minLength: 3, maxLength: 4 });
  var secondWord = Medium({ exactly: 1, Length: 5 });
  var thirdWord = Hard({ exactly: 1, minLength: 6 });

  return (
    <div>
      <h2>Please choose between these 3 words: </h2>
      <button onClick={onWordChoose} value={firstWord}>
        Eazy: {firstWord}
      </button>
      <button onClick={onWordChoose} value={secondWord}>
        Medium: {secondWord}
      </button>
      <button onClick={onWordChoose} value={thirdWord}>
        Hard: {thirdWord}
      </button>

      <p>{playerSelection}</p>
    </div>
  );
};

export default WordChoose;
