import React, { useState } from "react";

import "./App.css";
import PageSwitcher from "./components/PageSwitcher";
import { QueryClient, QueryClientProvider } from "react-query";
import Game from "./components/Pages/Draw";
import Guess from "./components/Pages/Guess";

const queryClient = new QueryClient();

const App = () => {
  const [isFinishedDrawing, setIsFinishedDrawing] = useState(false);
  const [playerDrawing, setPlayerDrawing] = useState();
  const [playerSelection, setPlayerSelection] = useState("");

  const onFinishDrawingHandler = (props) => {
    setPlayerDrawing(props[0]);
    setPlayerSelection(props[1]);
    setIsFinishedDrawing(true);
  };

  return (
    <div>
      {/* <QueryClientProvider client={queryClient}>
        <PageSwitcher />
      </QueryClientProvider> */}
      {
        <Game onPlayerFinishDrawing={onFinishDrawingHandler} />
      }
      {
        <div>
          <Guess
            value={playerDrawing}
            word={playerSelection}
            finishedDraw={isFinishedDrawing}
          />
        </div>
      }
    </div>
  );
};

export default App;
