import React, { useState, useRef } from "react";
import GameManager from "./GameManager";

const Guess = (props) => {
  const [userGuess, setUserGuess] = useState("");
  const [isCorrectGuess, setIsCurrectGusee] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [isFinishedDraw, setIsFinishedDraw] = useState(true);

  const userGuessHandler = (event) => {
    setUserGuess(event.target.value);
  };

  const submitHandler = () => {
    setIsCurrectGusee(userGuess === props.word);
    setErrorMessage("try again!");
    setUserGuess("");
  };

  return (
    <div>
      {!isCorrectGuess && (
        <div>
          {!isFinishedDraw && (
            <div>
              <h2>Wait to finish the drawing</h2>
            </div>
          )}
          {isFinishedDraw && (
            <div>
              <h4>Guess the drawing</h4>
              <p>{errorMessage}</p>
              <input
                type="text"
                value={userGuess}
                onChange={userGuessHandler}
              />
              <button onClick={submitHandler}>Click</button>
              <div>
                <img src={props.value} />
              </div>
            </div>
          )}
        </div>
      )}
      {isCorrectGuess && <h4>You guessed the word! ({props.word})</h4>}
    </div>
  );
};

export default Guess;
